AND cdevip in
#{item}

mapper.delete:
DELETE tem FROM workbench_alert_info_temporary tem WHERE EXISTS(
	SELECT '' FROM workbench_alert_info info WHERE tem.alert_no = info.alert_no
)

mapper.select:
select count(id) from workbench_alert_info
where title = #{title,jdbcType=VARCHAR} and description = #{description,jdbcType=VARCHAR}
  and dev_ip = #{devIp,jdbcType=VARCHAR} and priority = #{priority,jdbcType=VARCHAR}
  and csrcip = #{csrcip,jdbcType=VARCHAR} and cdstip = #{cdstip,jdbcType=VARCHAR}
  and stage >= 3

SELECT COUNT(u.id) FROM workbench_alert_user u
INNER JOIN workbench_alert_info i ON u.alert_id = i.id
WHERE i.title = #{title,jdbcType=VARCHAR} and i.description = #{description,jdbcType=VARCHAR}
  and i.dev_ip = #{devIp,jdbcType=VARCHAR} and i.priority = #{priority,jdbcType=VARCHAR}
  and i.csrcip = #{csrcip,jdbcType=VARCHAR} and i.cdstip = #{cdstip,jdbcType=VARCHAR}

update.foreach:
update workbench_alert_info
set
status = #{item.status,jdbcType=CHAR},
stage = #{item.stage,jdbcType=CHAR}

	,recurrent_id = #{item.recurrentId,jdbcType=VARCHAR}


	,alert_user_id = #{item.alertUserId,jdbcType=VARCHAR}

where id = #{item.id,jdbcType=VARCHAR}

update workbench_alert_info
set
merge_alert_no = #{item.mergeAlertNo,jdbcType=VARCHAR}
where id = #{item.id,jdbcType=VARCHAR}

update workbench_alert_info i
set i.num = i.num  + #{item.num,jdbcType=INTEGER},
i.is_reappear = if((i.is_epicycle_end = '1'),'1',i.is_reappear),
i.stage = case when (i.is_epicycle_end = '1') then i.stage+1
else i.stage end,
i.create_time2 = case when (i.is_epicycle_end = '1' and i.stage = '2') then #{item.createTime,jdbcType=TIMESTAMP}
else i.create_time2 end,
i.create_time3 = case when (i.is_epicycle_end = '1' and i.stage = '3') then #{item.createTime,jdbcType=TIMESTAMP}
else i.create_time3 end,
i.create_time4 = case when (i.is_epicycle_end = '1' and i.stage = '4') then #{item.createTime,jdbcType=TIMESTAMP}
else i.create_time4 end,
i.space1 = case when (i.is_epicycle_end = '1' and i.stage = '5') then #{item.createTime,jdbcType=TIMESTAMP}
	when (i.is_epicycle_end = '1' and i.stage > '5') then CONCAT(i.space1,';',#{item.createTime,jdbcType=TIMESTAMP})
	else i.space1 end,
i.is_epicycle_end = if((i.is_epicycle_end = '1'),'0',i.is_epicycle_end)
where
i.`status` != '0'
and i.is_close = '0'
and i.is_del = '0'
and i.title = #{item.title,jdbcType=VARCHAR}
and i.priority = #{item.priority,jdbcType=CHAR}
and i.description = #{item.description,jdbcType=VARCHAR}
and i.dev_ip = #{item.devIp,jdbcType=VARCHAR}
and i.csrcip = #{item.csrcip,jdbcType=VARCHAR}
and i.cdstip = #{item.cdstip,jdbcType=VARCHAR}

mapper.select:
SELECT t.userId,t.email,t.mobilePhoneNo,t.userName,t.userType,t.sendType, string_agg(t.id, ',') AS id, string_agg(CONCAT_WS('@_ssp_@',t.title,t.priority,t.dev_ip,t.description), '#_ssp_#') AS title, count(1) AS count, string_agg(CASE WHEN t.countType = '1' THEN t.id ELSE NULL END, ',') AS id1, string_agg(CASE WHEN t.countType = '1' THEN CONCAT_WS('@_ssp_@',t.title,t.priority,t.dev_ip,t.description) ELSE NULL END, '#_ssp_#') AS title1, SUM(CASE WHEN t.countType = '1' THEN 1 ELSE 0 END) AS count1, string_agg(CASE WHEN t.countType = '2' THEN t.id ELSE NULL END, ',') AS id2, string_agg(CASE WHEN t.countType = '2' THEN CONCAT_WS('@_ssp_@',t.title,t.priority,t.dev_ip,t.description) ELSE NULL END, '#_ssp_#') AS title2, SUM(CASE WHEN t.countType = '2' THEN 1 ELSE 0 END) AS count2, string_agg(CASE WHEN t.countType = '3' THEN t.id ELSE NULL END, ',') AS id3, string_agg(CASE WHEN t.countType = '3' THEN CONCAT_WS('@_ssp_@',t.title,t.priority,t.dev_ip,t.description) ELSE NULL END, '#_ssp_#') AS title3, SUM(CASE WHEN t.countType = '3' THEN 1 ELSE 0 END) AS count3 from ( SELECT al.id,al.title, al.description,al.priority,al.dev_ip, al.email,al.userId,al.userName,al.notice_type,al.stage,al.mobilePhoneNo,al.sendType,al.userType, CASE WHEN count <= ${secondInterval} THEN '1' WHEN count > ${secondInterval} AND count <= ${thirdInterval} THEN '2' WHEN count > ${thirdInterval} THEN '3' ELSE '' END AS countType from ( SELECT ba.id,a.title, a.description,sd.dict_label AS priority,a.dev_ip, u.email,u.user_type AS userType,u.user_id AS userId,u.nick_name AS userName,u.phonenumber AS mobilePhoneNo,ba.notice_type,ba.stage, CONCAT( CASE WHEN st.wechat_status = '1' THEN '1,' ELSE '' END, CASE WHEN st.phone_status = '1' THEN '2,' ELSE '' END, CASE WHEN st.email_status = '1' THEN '3,' ELSE '' END ) AS sendType, CASE WHEN to_char(ba.start_time, 'YYYY-MM-DD') > to_char(ba.end_time, 'YYYY-MM-DD') THEN 0 WHEN to_char(ba.start_time, 'YYYY-MM-DD') = to_char(ba.end_time, 'YYYY-MM-DD') THEN floor(extract(epoch from (ba.end_time - ba.start_time)) / 60) ELSE floor(extract(epoch from (to_timestamp(to_char(ba.start_time, 'YYYY-MM-DD') || ' ' || to_char(ba.end_time, 'HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') - ba.start_time)) / 60) + (SELECT COUNT(1) FROM s_workday WHERE CAST(workday AS timestamp) > ba.start_time AND CAST(workday AS timestamp) < ba.end_time) * #{timeDifference} END AS count from ( SELECT au.id, au.alert_id, au.user_id, au.assign_time, au.status, au.stage, au.assign_id, au.notice_type, CASE WHEN w1.id is NULL THEN (SELECT to_timestamp(MIN(workday) || #{workBeginTime}, 'YYYY-MM-DD HH24:MI:SS') from s_workday WHERE workday > au.start_time_) ELSE au.start_time_ END AS start_time, CASE WHEN w2.id is NULL THEN (SELECT to_timestamp(MAX(workday) || #{workEndTime}, 'YYYY-MM-DD HH24:MI:SS') from s_workday WHERE workday < au.end_time_) ELSE au.end_time_ END AS end_time from (SELECT *, CASE WHEN assign_time < to_timestamp(to_char(assign_time, 'YYYY-MM-DD') || #{workBeginTime}, 'YYYY-MM-DD HH24:MI:SS') THEN to_timestamp(to_char(assign_time, 'YYYY-MM-DD') || #{workBeginTime}, 'YYYY-MM-DD HH24:MI:SS') WHEN assign_time > to_timestamp(to_char(assign_time, 'YYYY-MM-DD') || #{workEndTime}, 'YYYY-MM-DD HH24:MI:SS') THEN to_timestamp(to_char(date_add('day', 1, assign_time), 'YYYY-MM-DD') || #{workBeginTime}, 'YYYY-MM-DD HH24:MI:SS') ELSE assign_time END AS start_time_, CASE WHEN now() < to_timestamp(to_char(now(), 'YYYY-MM-DD') || #{workBeginTime}, 'YYYY-MM-DD HH24:MI:SS') THEN to_timestamp(to_char(date_add('day', -1, now()), 'YYYY-MM-DD') || #{workEndTime}, 'YYYY-MM-DD HH24:MI:SS') WHEN now() > to_timestamp(to_char(now(), 'YYYY-MM-DD') || #{workEndTime}, 'YYYY-MM-DD HH24:MI:SS') THEN to_timestamp(to_char(now(), 'YYYY-MM-DD') || #{workEndTime}, 'YYYY-MM-DD HH24:MI:SS') ELSE now() END AS end_time_ from workbench_alert_user where status = '2') au LEFT JOIN s_workday w1 on w1.workday = to_char(au.start_time_, 'YYYY-MM-DD') LEFT JOIN s_workday w2 on w2.workday = to_char(au.end_time_, 'YYYY-MM-DD') ) ba INNER JOIN workbench_alert_info a on ba.alert_id = a.id and a.is_close = '0' and a.is_del = '0' and a.is_night = '0' INNER JOIN sys_user u on ba.user_id = u.user_id left join s_user_send_type st on st.user_id = u.user_id LEFT JOIN sys_dict_data sd ON sd.dict_value = a.priority and sd.dict_type = 'alert_level' and sd.status = '1' HAVING count > ${firstInterval}
union SELECT ba.id,a.title, a.description,dd.dict_label AS priority,a.dev_ip, u.email,u.user_type AS userType,u.user_id AS userId,u.nick_name AS userName,u.phonenumber AS mobilePhoneNo,ba.notice_type,ba.stage, CONCAT( CASE WHEN st.wechat_status = '1' THEN '1,' ELSE '' END, CASE WHEN st.phone_status = '1' THEN '2,' ELSE '' END, CASE WHEN st.email_status = '1' THEN '3,' ELSE '' END ) AS sendType, floor(extract(epoch from (ba.end_time - ba.start_time)) / 60) AS count from ( SELECT au.id, au.alert_id, au.user_id, au.assign_time, au.status, au.stage, au.assign_id, au.notice_type, au.assign_time AS start_time, now() AS end_time from workbench_alert_user au where status = '2' ) ba INNER JOIN workbench_alert_info a on ba.alert_id = a.id and a.is_close = '0' and a.is_night = '1' INNER JOIN sys_user u on ba.user_id = u.user_id left join s_user_send_type st on st.user_id = u.user_id LEFT JOIN sys_dict_data dd ON dd.dict_value = a.priority and dd.dict_type = 'alert_level' and dd.status = '1' HAVING count > ${firstInterval}
) al HAVING countType != al.notice_type or al.notice_type is NULL ) t where 1=1
and t.stage BETWEEN 1 AND 3
and t.stage >= '4'
GROUP BY t.userId

select GROUP_CONCAT(a.id) id, GROUP_CONCAT(a.title separator '、') title, count(1) count,GROUP_CONCAT(CONCAT_WS('@_ssp_@',a.title,a.priority,a.dev_ip,a.description) SEPARATOR '#_ssp_#' ) Space1
from (
	SELECT ai.id,ai.title,ai.priority,ai.dev_ip,ai.description,if(
	(
		SELECT COUNT(1)
		FROM sys_user u
		WHERE u.status = '1' and
		EXISTS (
			SELECT 1 FROM sys_user_role uf, sys_role_menu fm, sys_menu m WHERE uf.role_id = fm.role_id AND fm.menu_id = m.menu_id
			AND m.`status` = '1' AND m.perms = 'workflow:alert' AND uf.user_id = u.user_id
		)
		/*AND EXISTS(
			SELECT '' FROM workbench_schedule_task wt WHERE wt.status = '1' and wt.executor_id = u.user_id AND (NOW() BETWEEN wt.begain_time AND wt.end_time)
		)*/
		AND EXISTS (
			SELECT 1 FROM workbench_alert_user au1 WHERE au1.user_id = u.user_id AND au1.alert_id = ai.id and au1.stage = ai.stage
		)
	) >= #{num,jdbcType=INTEGER},'Y','N') flg
	from workbench_alert_user au
	INNER JOIN workbench_alert_info ai ON ai.id = au.alert_id
	where au.id in

		#{item,jdbcType=VARCHAR}

) a
where

	a.flg = 'Y'


	a.flg = 'N'

SELECT ai.id,ai.title,ai.stage,ai.is_epicycle_end,ai.is_reappear FROM workbench_alert_info ai LEFT JOIN workbench_alert_user wu ON ai.id = wu.alert_id AND ai.stage = wu.stage WHERE ai.id in
#{item,jdbcType=VARCHAR}
GROUP BY ai.id

