poch from (ba.end_time - ba.start_time)) / 60) ELSE floor(extract(epoch from (to_timestamp(to_char(ba.start_time, 'YYYY-MM-DD') || ' ' || to_char(ba.end_time, 'HH24:MI:SS'), 'YYYY-MM-DD HH24:MI:SS') - ba.start_time)) / 60) + (SELECT COUNT(1) FROM s_workday WHERE CAST(workday AS timestamp) > ba.start_time AND CAST(workday AS timestamp) < ba.end_time) * #{timeDifference} END AS count from ( SELECT au.id, au.alert_id, au.user_id, au.assign_time, au.status, au.stage, au.assign_id, au.notice_type, au.start_time_ AS start_time, au.end_time_ AS end_time from (SELECT *, CASE WHEN assign_time < to_timestamp(to_char(assign_time, 'YYYY-MM-DD') || #{workBeginTime}, 'YYYY-MM-DD HH24:MI:SS') THEN to_timestamp(to_char(assign_time, 'YYYY-MM-DD') || #{workBeginTime}, 'YYYY-MM-DD HH24:MI:SS') WHEN assign_time > to_timestamp(to_char(assign_time, 'YYYY-MM-DD') || #{workEndTime}, 'YYYY-MM-DD HH24:MI:SS') THEN to_timestamp(to_char(date_add('day', 1, assign_time), 'YYYY-MM-DD') || #{workBeginTime}, 'YYYY-MM-DD HH24:MI:SS') ELSE assign_time END AS start_time_, CASE WHEN now() < to_timestamp(to_char(now(), 'YYYY-MM-DD') || #{workBeginTime}, 'YYYY-MM-DD HH24:MI:SS') THEN to_timestamp(to_char(date_add('day', -1, now()), 'YYYY-MM-DD') || #{workEndTime}, 'YYYY-MM-DD HH24:MI:SS') WHEN now() > to_timestamp(to_char(now(), 'YYYY-MM-DD') || #{workEndTime}, 'YYYY-MM-DD HH24:MI:SS') THEN to_timestamp(to_char(now(), 'YYYY-MM-DD') || #{workEndTime}, 'YYYY-MM-DD HH24:MI:SS') ELSE now() END AS end_time_ from workbench_alert_user where status = '2') au ) ba INNER JOIN workbench_alert_info a on ba.alert_id = a.id and a.is_close = '0' and a.is_del = '0' and a.is_night = '0' INNER JOIN sys_user u on ba.user_id = u.user_id left join s_user_send_type st on st.user_id = u.user_id LEFT JOIN sys_dict_data sd ON sd.dict_value = a.priority and sd.dict_type = 'alert_level' and sd.status = '1' HAVING count > ${firstInterval}
